#include "Token.hpp"

//--------------------------------------------------------------------------------------------------
///
//--------------------------------------------------------------------------------------------------
Token::Token( Token::Kind kind, size_t start, size_t end )
    : m_kind( kind )
    , m_start( start )
    , m_end( end )
{
}

//--------------------------------------------------------------------------------------------------
///
//--------------------------------------------------------------------------------------------------
Token::Kind Token::kind() const
{
    return m_kind;
}

//--------------------------------------------------------------------------------------------------
///
//--------------------------------------------------------------------------------------------------
size_t Token::start() const
{
    return m_start;
}

//--------------------------------------------------------------------------------------------------
///
//--------------------------------------------------------------------------------------------------
size_t Token::end() const
{
    return m_end;
}
